const { runIfMain } = require('./lib');

const input = ``;

function firstUniqueMarkerPosition(input, markerLength) {
  const check = {};
  let count = 0;

  function add(char) {
    if (!check[char]) count += 1;
    check[char] = (check[char] || 0) + 1;
  }
  function remove(char) {
    check[char] -= 1;
    if (!check[char]) count -= 1;
  }

  for (let i = 0; i < markerLength; i += 1) {
    add(input[i]);
  }

  for (let i = markerLength; i < input.length; i += 1) {
    if (count === markerLength) return i;
    remove(input[i - markerLength]);
    add(input[i]);
  }

  return -1;
}

function part1(input) {
  return firstUniqueMarkerPosition(input, 4);
}

function part2(input) {
  return firstUniqueMarkerPosition(input, 14);
}

part1.desc = '';
